/* 	UI dispatcher v0.1b 08/10/2014
	Recives changes from UI and actions them 
	Now uses context.global.dataPionts to hold state */
var obj = JSON.parse(msg.payload);
delete msg.payload;
delete msg._session;

// toggle switch tsw-1	- output 1 
if(obj.id=="tsw-1"){
	context.global.dataPionts["tsw-1"].v=obj.v;
	msg.payload = {"id": obj.id, "v": obj.v};	
	return [msg, null, null, null, null];

// toggle switch tsw-2	- output 2 
} else if(obj.id=="tsw-2"){
    context.global.dataPionts["tsw-2"].v=obj.v;
	msg.payload = {"id": obj.id, "v": obj.v};	
	return [null, msg, null, null, null];

// slider  sld-1 		- output 3 	
} else if(obj.id=="sld-1"){
	context.global.dataPionts["sld-1"].v=obj.v;
	msg.payload = {"id": obj.id, "v": obj.v};	
	return [null, null, msg, null, null, null];

// txt popup txt-1 		- output 4 	
} else if(obj.id=="txt-1"){
	context.global.dataPionts["txt-1"].v=obj.v;
	msg.payload = {"id": obj.id, "v": obj.v};	
	return [null, null, null, msg, null, null];

// init request 		- output 5 
} else if(obj.id=="init"){	
	context.global.dataPionts["shd"].v = JSON.stringify(context.global.schedule);
	msg.payload = {id:"init", v: context.global.dataPionts}; 
	return [null, null, null, null, msg, null];

// save shedule 		- output 6 
} else if(obj.id=="shd-save"){
	context.global.schedule = null;
	//context.global.schedule = JSON.stringify(obj.v);
	context.global.schedule = obj.v;
	msg.payload = obj.v;
	return [null, null, null, null, null, msg];

//  do nothing
} else {
	return [null, null, null, null, null, null];
}

context.count = context.count || parseInt(context.global.dataPionts["sld-1"].v); 
context.count = parseInt(context.count) + parseInt(msg.topic);
if(context.count>100){context.count=0;}
context.global.dataPionts["sld-1"].v=context.count; // used by ui init
msg.payload = {id:"sld-1", v: context.count}; 
return msg;

if(context.global.dataPionts["tsw-1"].v==1){
	context.global.dataPionts["tsw-1"].v=0;
} else {
	context.global.dataPionts["tsw-1"].v=1;
}
msg.payload = {id:"tsw-1", v: context.global.dataPionts["tsw-1"].v}; 
return msg;

msg.payload = {id:"val-1", v: parseFloat(msg.topic).toFixed(1)+"C"};
context.global.dataPionts["val-1"].v=parseFloat(msg.topic).toFixed(1)+"C"; // used by ui init
return msg;

if(context.global.dataPionts["tsw-2"].v==1){
	context.global.dataPionts["tsw-2"].v=0;
} else {
	context.global.dataPionts["tsw-2"].v=1;
}
msg.payload = {id:"tsw-2", v: context.global.dataPionts["tsw-2"].v}; 
return msg;

var statusMsg = {stMsg: msg.topic, dur:0, pri:1};
msg.payload = {id:"sta-1", v:statusMsg};
return msg;

context.global.dataPionts = {
"tsw-1": {"v": 1},
"tsw-2": {"v": 0},
"sld-1": {"v": 10},
"val-1": {"v": 20.0},
"txt-1": {"v": null},
"sta-1": {"v": "init message"},
"shd" : {"v": null},
};
return msg;

 // Web User created Scheduler v0.1b 
context.dayOfWeek = context.dayOfWeek || null; 
context.scheduledState = context.scheduledState || [];
//console.log("len:"+context.scheduledState.length); 
if(context.scheduledState.length==0){
	for(var i = 0; i < 21; i++) {
	   context.scheduledState[i]=null;
	}
}
var d = new Date();
var _tm = "d:"+d.getDay()+" h:"+d.getHours()+" m:"+ d.getMinutes()+" s:"+ d.getSeconds();
msg.payload = "Scheduled run";

function getEvents(){

	console.log("schedule run at:"+_tm);

	for (var item in context.global.schedule.items) {
		var itemI =  parseInt(item);
		var event = context.global.schedule.items[item];
		//console.log("id:"+event.id+" dow:"+event.dofWeek[d.getDay()]+" state:"+ context.scheduledState[itemI]+" type:"+ typeof  itemI);

		// Check for scheduled Start event		
		var hrmin=event.startTime.split(":");
		if(parseInt(hrmin[0]) ==d.getHours() &&  parseInt(hrmin[1]) ==d.getMinutes() &&  context.scheduledState[itemI]==null &&  event.dofWeek[d.getDay()] ==1){
			msg.payload = {id: event.id, v:event.startValue};
			context.scheduledState[itemI]=1;
			console.log("id:"+event.id+" start scheduled state:"+context.scheduledState[itemI]);
			return
		}
		// Check for scheduled End event
		hrmin=event.endTime.split(":");
		if(parseInt(hrmin[0]) ==d.getHours() &&  parseInt(hrmin[1]) ==d.getMinutes() &&  context.scheduledState[itemI]==1 &&  event.dofWeek[d.getDay()] ==1){
			msg.payload = {id: event.id, v:event.endValue};
			context.scheduledState[itemI]= null;
			return
		}
	}
}

function scheduleReset(){
	if(context.dayOfWeek != d.getDay()){

		if(context.dayOfWeek == null){
			console.log( "reset scheduel due to node-red restart");	
		} else {
			console.log( "reset scheduel due to change of day:"+d.getDay());		
		}

		context.dayOfWeek = d.getDay();
		for (var item in context.global.schedule.items) {
			var itemI =  parseInt(item);
			if(context.scheduledState[itemI]==1 && context.global.schedule.items[item].noEndTime==true ){
				context.scheduledState[itemI]=null;
				console.log("schedule item:"+itemI+" reset");
			}
		}
		msg.payload = "schedule reset";
	}
}

scheduleReset();
getEvents();
return msg;
